import React, { useState } from 'react';
import { connect } from "react-redux";



const Favorites = ({ favorites }) => {
    const [removeFav, setRemoveFat] = useState(false);

    const handleRemoveFav = (id) => {
        setRemoveFat(false);
        this.props.removeFav(id)
    }

    return <div className="favorites-container">
        <h1 className='fav-title'>My Favorites Characters</h1>
        {favorites.map((favorite) => (
          <div className="fav-list-img">
          <div className="fav-list">
              <img className="fav-img" src={favorite.image} alt="img" /> 
          <div className="fav-list-items">
          <h2>Name: {favorite.name}</h2>  
          <h3>Status: {favorite.status}</h3> 
          <h3>Specie: {favorite.species}</h3> 
          <h3>Gender: {favorite.gender}</h3> 
          <h3>Origin: {favorite.origin}</h3>  
          </div>
          </div>
          <div className="detail-img">
          </div>
          <div>
            </div>
                {removeFav ? (
                    <button onClick={() => handleRemoveFav(this.props.id)}>Remove to Favorites</button>
                ) : ( 
                     <button onClick={() => this.props.goBack()}>Go Home</button>   
                )}
            </div>
        ))}
    </div>
};

const mapStateToProps = (state) => {
    return {
        favorites: state.myFavorites,
    };
};

export default connect(mapStateToProps)(Favorites);
