//import SearchBar from "./SearchBar";
 import React, { useState, useEffect } from "react";
 import { Link } from "react-router-dom";
 import { connect } from "react-redux";
 import { addFavorite, removeFavorite } from "../redux/actions"
import axios from 'axios';


 const Card = ( { name, gender, onClose, species, image, id }) => {
   const [isFav, setIsFav ] = useState(false);


    const handleFavorite = (id) => {
      if(isFav) {
         setIsFav(false);
         removeFavorite(id);
         axios.delete(`http://localhost:3001/rickandmorty/fav/${id}`)
      .then((res) => console.log('ok'));
      } else {
         setIsFav(true);
         addFavorite();
         axios.post("http://localhost:3001/rickandmorty/fav", {
            name, gender, onClose, species, image, id
         })
         .then((res) => console.log('ok'));
      }

   };


   //FOR EACH
   useEffect(() => {
      this.props.myFavorites.forEach((fav) => {
         if (fav.id === id) {
            setIsFav(true);
         }
      });
   }, [id, this.props.myFavorites]);

   //BUCLE FOR
      // useEffect(() => {
      //    for (let i = 0; i < .myFavorites.length; i++){
      //       const fav = .myFavorites[i];
      //       if(fav.id === .id){
      //          setIsFav(true);
      //          break; // Una vez que se encuentra el favorito, se puede salir del bucle.
      //       }
      //    }
      // }, []);




   return (
      <div className="card">
         <div className="card-header">
            <img src={image} alt={name} className="card-image"></img>
         </div>
         <div className="card-body-btn">
            <div className="card-body">
                  <h3 className="card-number">{id}</h3>
                  <h2 className="card-title">{name}</h2>

               <Link to={`/detail/${id}`}>
                  <p className="card-see-btn"></p>
               </Link>

               {
            isFav ? (
               <button className="add-fav-button" onClick={handleFavorite}>
                  <p className="add-fav-title">
                     Added to Favorites
                     <br/>
                     <span className="add-fav-heart-red">‚ù§Ô∏è</span>
                  </p>

                  </button>
            ) : (
               <button className="add-fav-button" onClick={handleFavorite}>
                  <p className="added-fav-title">
                     Add to Favorites
                     <br/>
                     <span className="add-fav-heart-white">ü§ç</span>
                  </p>
                  </button>
            )
          }
               </div>

            </div>
               <div>
            <button onClick={() => onClose(id)} className="card-close-btn">X</button>
            </div>
            </div>

   );
}

   const mapStateToProps = (props) => {
      return {
      myFavorites: props.myFavorites,
      };
   };

   const mapDispatchToProps = (dispatch) => {
      return {
         addFavorite: (props) => {
            dispatch(addFavorite(props))
         },  
         removeFavorite: (id) => {
            dispatch(removeFavorite(id))
         }
      }
   }

export default connect(mapStateToProps, mapDispatchToProps)(Card);
