//import { ADD_FAVORITES } from "./actions_types";
import { REMOVE_FAVORITE } from "./actions_types";


export const addFavorite = (id) => {
    return {
        type: 'ADD_FAVORITES',
        payload: id,
    }
}

// export const removeFavorite = (id) => {
