export const removeFavorite = (id) => async (dispatch) => {
    try {
      const response = await fetch(`http://localhost:3001/rickandmorty/fav/${id}`, {
        method: 'DELETE',
      });
      const data = await response.json();
      dispatch({
        type: REMOVE_FAVORITE,
        payload: id,
      });
    } catch (error) {
      console.log(error);
    }
  };
